const useFormatDate = (date) => {
    let rawDate = new Date(date)

    let month = format(rawDate.getMonth() + 1)
    let day = format(rawDate.getDate())
    let year = format(rawDate.getFullYear())

    return `${month}/${day}/${year}`
}

export default useFormatDate
